sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();

	// Query the database for NeedIt Task records with Due date field values in the future within 24 hours
	//  Only return NeedIt Task records that do not have
	// a State field value of Closed complete.
	var dueSoon = new GlideRecord('x_58872_needit_needit_task');
	dueSoon.addQuery('due_date','>=',rightNow);
    dueSoon.addQuery('due_date', '<', gs.hoursAgo(-24));
	dueSoon.addQuery('state','!=',3);

	dueSoon.query();
	// Write a log message for each due soon NeedIt Task Record
	while(dueSoon.next()){
		gs.info("Due Soon NeedIt Task record = " + dueSoon.number);
		gs.eventQueue('x_58872_needit.dueSoonNeeditTasks',dueSoon,dueSoon.number,gs.getUserName());
	}
